// owl carousel init
function initOwlCarousel() {
    jQuery('.news-slider').owlCarousel({
        loop: true,
        nav: true,
        navText: ['<i class="icon-angle-left" aria-hidden="true"></i>', '<i class="icon-angle-right" aria-hidden="true"></i>'],
        dots: false,
        responsiveClass: true,
        autoWidth: false,
        margin: 90,
        responsive: {
            0: {
                items: 1,
                autoWidth: true,
                margin: 36
            },
            768: {
                items: 2,
                margin: 36
            },
            1024: {
                items: 3,
                center: true
            }
        }
    });
}

// slick init
function initSlickCarousel() {
    jQuery('.text-slider').slick({
        slidesToScroll: 1,
        rows: 0,
        arrows: false,
        asNavFor: '.img-slider',
        fade: true,
        dots: true,

    });

    jQuery('.img-slider').slick({
        slidesToScroll: 1,
        rows: 0,
        arrows: false,
        asNavFor: '.text-slider',
        fade: true,
        autoplay: true,
        autoplaySpeed: 7000
    });

    jQuery('.blockquote-slider').slick({
        slidesToScroll: 1,
        slidesToShow: 1,
        rows: 0,
        arrows: false,
        fade: true,
        dots: false,
        asNavFor: '.logo-slider'
    });
    jQuery('.logo-slider').slick({
        slidesToScroll: 1,
        slidesToShow: 3,
        rows: 0,
        variableWidth: true,
        // variableWidth: false,
        focusOnSelect: true,
        arrows: false,
        asNavFor: '.blockquote-slider',
        dots: true,
        responsive: [{
            breakpoint: 768,
            settings: {
                slidesToShow: 1,
                // variableWidth: true
            }
        }]
    });

    ResponsiveHelper.addRange({
        '..767': {
            on: function () {
                jQuery('.slick-tabs').slick({
                    slidesToScroll: 1,
                    rows: 0,
                    prevArrow: '<button class="slick-prev"><i class="icon-angle-left"></i></button>',
                    nextArrow: '<button class="slick-next"><i class="icon-angle-right"></i></button>'
                });
            },
            off: function () {
                jQuery('.slick-tabs').slick('destroy');
            }
        }
    });

    jQuery('.criteria-slider').slick({
        slidesToScroll: 1,
        rows: 0,
        prevArrow: '<button class="slick-prev"><i class="icon-angle-left"></i></button>',
        nextArrow: '<button class="slick-next"><i class="icon-angle-right"></i></button>'
    });
}

// accordion menu init
function initAccordion() {

    ResponsiveHelper.addRange({
        '..1023': {
            on: function () {
                jQuery('.nav').slideAccordion({
                    opener: '.opener',
                    slider: 'ul',
                    animSpeed: 300
                });
            },
            off: function () {
                jQuery('.nav').slideAccordion('destroy');
            }
        }
    });

    jQuery('.faq-block').slideAccordion({
        opener: '.opener',
        slider: '.slide',
        animSpeed: 300
    });
}

// mobile menu init
function initMobileNav() {
    ResponsiveHelper.addRange({
        '..1023': {
            on: function () {
                jQuery('body').mobileNav({
                    menuActiveClass: 'nav-active',
                    menuOpener: '.nav-opener'
                });
            },
            off: function () {
                jQuery('body').mobileNav('destroy');
            }
        }
    });
}

// initialize smooth anchor links
function initAnchors() {
    new SmoothScroll({
        anchorLinks: 'a.to-down',
        extraOffset: 0,
        wheelBehavior: 'none'
    });

    new SmoothScroll({
        anchorLinks: 'a.fixed-link',
        extraOffset: 0,
        activeClasses: 'parent',
        wheelBehavior: 'none'
    });
}

// handle dropdowns on mobile devices
function initTouchNav() {
    jQuery('.flex-holder.accordion').each(function () {
        new TouchNav({
            navBlock: this,
            menuOpener: 'a.opener',
            menuItems: '.item',
            menuDrop: '.slide'
        });
    });
}

// content tabs init
function slickTabSlider() {
    var tabSlider = jQuery('.tab-slider');

    tabSlider.each(function () {
        var holder = jQuery(this);
        var hiddenClass = 'js-tab-hidden';
        var tabs = holder.find('.tab');
        var activeClass = 'active';
        var slider = holder.find('.slick-slider-frame');
        var currentLinksHolder = null;

        slider.on('init afterChange', function () {
            currentLinksHolder = slider.find('.slick-current');

            tabs.removeClass(activeClass);
            tabs.addClass(hiddenClass);

            currentLinksHolder.tabset({
                activeClass: 'active',
                tabLinks: 'a',
                event: 'click'
            });
        }).on('beforeChange', function () {
            currentLinksHolder.find('a').removeClass(activeClass);
        });

        slider.slick({
            slidesToScroll: 1,
            rows: 0,
            prevArrow: '<button class="slick-prev"><i class="icon-angle-left"></i></button>',
            nextArrow: '<button class="slick-next"><i class="icon-angle-right"></i></button>'
        });
    });
}

// initialize fixed blocks on scroll
function initStickyScrollBlock() {
    jQuery('.fixed-menu').stickyScrollBlock({
        setBoxHeight: false,
        activeClass: 'fixed-position',
        positionType: 'fixed',
        extraTop: function () {
            var totalHeight = 100;
            jQuery('#header').each(function () {
                totalHeight += jQuery(this).outerHeight();
            });
            return totalHeight;
        },
        showAfterScrolled: true
    });
}

// content tabs init
function initTabs() {

    jQuery('.ivent-holder').tabset({
        tabLinks: 'a',
        addToParent: true
    });

    jQuery('.partners-tabset').tabset({
        tabLinks: 'a',
        addToParent: true
    });

    jQuery('.payment-tabset').tabset({
        tabLinks: 'a',
        addToParent: true
    });

    ResponsiveHelper.addRange({
        '..767': {
            on: function () {
                jQuery('.tabset').tabset({
                    tabLinks: 'a',
                    defaultTab: true
                });
            },
            off: function () {
                jQuery('.tabset').tabset('destroy');
            }
        }
    });
}

// load more init
function initLoadMore() {
    jQuery('.load-item').loadMore({
        linkSelector: 'a.load-more'
    });
}

